import React, { useState } from 'react';
import './Task.css';

const Task = (props) => {

  const onCheckBoxClick = () => {
      const updatedTask = {
          title: props.title,
          content: props.content,
          complete: !props.complete
      }
      props.onUpdateTask(updatedTask)
    }


  // Component functions always return JSX
  return (
    <div className='task'>
      {props.complete? <h3><s>{props.title}</s></h3> : <h3>{props.title}</h3>}
      {props.complete? <p><s>{props.content}</s></p> : <p>{props.content}</p>}
      {/* Note that we will get into what's happening in this line in the Events lesson, coming up next! */}
      <input
            name="isComplete"            
            type="checkbox"
            checked={props.complete}
            onChange={changeComplete} 
        />
    </div>
  );
};

export default Task;
