import React, { useState } from 'react';

const Task = (props) => {

  const [complete, setComplete] = useState(false);

  const changeComplete = () => setComplete(!complete);

  // Component functions always return JSX
  return (
    <div className='task'>
      {complete? <h3>{props.title}</h3> : <h3>{props.title}</h3>}
      <p>{props.content}</p>
      {/* Note that we will get into what's happening in this line in the Events lesson, coming up next! */}
      <input
            name="isComplete"            
            type="checkbox"
            checked={complete}
            onChange={changeComplete} 
        />
    </div>
  );
};

export default Task;
